// import './App.css';
import './lens-flare.css';

function App() {

	return (
    <div class="main">
		<div id="centerpoint"></div>
    <div class="lensflare">
        <div class="source">
            <div class="source-spread"></div>
            <div class="source-beam"></div>
        </div>

      <div class="c1"></div>
      <div class="c2"></div>
      <div class="c3"></div>
      <div class="c4"></div>
      <div class="c5"></div>
      <div class="c6"></div>
      <div class="c7"></div>
      <div class="c8"></div>
      <div class="c9"></div>
      <div class="c10"></div>
      <div class="c11"></div>
      <div class="c12"></div>
      <div class="c13"></div>
      <div class="c14"></div>
    </div>
    </div>

	);
}



var source = {'x': 13, 'y':20}
var delta;
var srcDistance, cpoint;
var flareVisible = true;
var origin;
$(document).ready(function () {
  calculateLensFlare();
});

$(document).on('scroll',function() {
  calculateLensFlare();

})

function calculateLensFlare() {
  if(source.y*0.5 - px2vh($(window).scrollTop()/4) > 0) {

    if(!flareVisible) {
      $('.lensflare').css('display','block');
      flareVisible = true;
    }

    var i = 0;

    cpoint = {
      'x': px2vw($('#centerpoint').offset().left),
      'y': px2vh($('#centerpoint').offset().top + 25)
    }

    delta = {
      'x':(cpoint.x-source.x)/10, 
      'y':(cpoint.y-source.y)/12,
    };
    _.each($('.lensflare')[0].children, function(flare) {
      $(flare).css('left',(source.x + (delta.x * i)) +'vw');
      var hack = 1;
      if(i == 0) {hack = 0;}
      $(flare).css('top','calc(' + (source.y + (delta.y * i)) +'vw - ' + ($(window).scrollTop() * (delta.x/6 * hack )) + 'px)');

    i++;
    //console.log($(flare));
  })
  } else {
    $('.lensflare').css('display','none');
      flareVisible = false;
  }
}

function px2vw(px) {
  return px/$(window).width() * 100;
}

function px2vh(px) {
  return px/$(window).height() * 100;
}



export default App;
